    public function grid($pg=1)
    {
        $num = ($pg-1)*20;
        $regin_type = $this->input->get('region_type') ? $this->input->get('region_type') : 0;
        $parent_id = $this->input->get('parent_id') ? $this->input->get('parent_id') : 0;
        $config['suffix'] = $this->pageGetParam($this->input->get());
        $config['base_url'] = base_url('region/grid');
        $config['total_rows'] = $this->region->total($parent_id, $regin_type);
        $config['uri_segment'] = 3;
        $this->pagination->initialize($config);
        $data['pg_link'] = $this->pagination->create_links();
        $data['page_list'] = $this->region->page_list($num, $parent_id, $regin_type);
        $data['all_rows'] = $config['total_rows'];
        $data['pg_now'] = $pg;
        $data['regin_type'] = $this->reginType;
        $this->load->view('region/grid', $data);
    }
    
    public function add()
    {
        $this->load->view('region/add');
    }
    
    public function addPost()
    {
        $error = $this->validate();
        if (!empty($error)) {
            $this->error('region/add', $this->input->get(), $error);
        }
        $this->db->trans_start();
        $resultId = $this->region->insertRegion($this->input->post());
        $this->db->trans_complete();
    
        if ($resultId) {
            $this->success('region/grid', $this->input->get(), '保存成功！');
        } else {
            $this->error('region/add/', $this->input->get(), '保存失败！');
        }
    }
    
    public function edit($id)
    {
        $result = $this->region->findById($id);
        if($result->num_rows() <= 0) {
            $this->redirect('region/grid');
        }
        $data['region'] = $result->row();
        $this->load->view('region/edit', $data);
    }
    
    public function editPost()
    {
        $id = $this->input->post('region_id');
        $error = $this->validate();
        if (!empty($error)) {
            $this->error('region/edit', $id, $error);
        }
        
        $this->db->trans_start();
        $resultId = $this->region->updateRegion($this->input->post());
        $this->db->trans_complete();
    
        if ($resultId) {
            $this->success('region/grid', $this->input->get(), '保存成功！');
        } else {
            $this->error('region/edit/'.$id, $this->input->get(), '保存失败！');
        }
    }
    
    public function validate()
    {
        $error = array();
        if ($this->validateParam($this->input->post('region_name'))) {
            $error[] = '中文名称不能为空';
        }
        if ($this->validateParam($this->input->post('region_pinyin'))) {
            $error[] = '英文全程不能为空';
        }
        if ($this->validateParam($this->input->post('region_abbr'))) {
            $error[] = '英文缩写不能为空';
        }
        return $error;
    }
    
    /**
     * 根据ajax反馈的父id，返回所有子集 （json格式）
     */
    public function select_children($parent_id=0)
    {
        $childrenData = array();
        if ($parent_id) {
            $childrenData = $this->region->children_of($parent_id);
        }
        echo json_encode($childrenData);exit;
    }
    
    public function autocompleteCity()
    {
        $result = $this->region->findByAutocomplete(2); //1.代表省；2.代表城市；3.代表区或县
        $cityArray = $result->result_array();
        echo json_encode($cityArray);exit;
    }
    
    public function ajaxGetRegion($pg = 1){
        $num = ($pg-1)*20;
        $config['suffix'] = $this->pageGetParam($this->input->get());
        $config['base_url'] = base_url('region/grid');
        $config['total_rows'] = $this->region->totalByArray($this->input->get());
        $config['uri_segment'] = 3;
        $this->pagination->initialize($config);
        $data['pg_list'] = $this->pagination->create_links();
        $data['page_list'] = $this->region->findByArray($num, $this->input->get());
        $data['all_rows'] = $config['total_rows'];
        $data['pg_now'] = $pg;
        
        echo json_encode(array(
            'status'=>true,
            'html'  =>$this->load->view('region/ajaxregion/ajaxRegionData', $data, true)
        ));exit;
    }
}